import React, { useState } from 'react'
import ReactDOM from 'react-dom'
import Square from './Square'

const Board = () => {
    const [boardArray, setBoardArray] = useState(Array(9).fill(null))
    const [XNext, setXNext] = useState(true)

    // returnerer x eller y
    const checkWinner = ()  => {
        debugger;
        const vinnerLinjer = [
            [0,1,2],
            [3,4,5],
            [6,7,8],
            [0,3,6],
            [1,4,7],
            [2,5,8],
            [2,4,6],
            [0,4,8]
        ]

        // sjekk boararray om det har en av disse linjene med bare en verdi
        vinnerLinjer.forEach(linje => function() {
            let a = linje[0]
            let b = linje[1]
            let c = linje[2]

            let x = boardArray[a]
            let y = boardArray[b]
            let z = boardArray[c]

            if (x !== null && y !== null && z !== null) {
                if (x === y && y === z) {
                    if (x === 'X') { 
                        alert("X VINNER")
                    } else {
                        alert("Y VINNER")
                    }
                }
            }
            // linje.forEach(tall => function() {
            //     let a = array[tall]
            // })
        })
        
    }

    const handleClick = (n) => {
        const newArray = boardArray.slice()
        if (XNext) {
            newArray[n] = 'X'
        } else {
            newArray[n] = '0'
        }
        setXNext(!XNext)

        setBoardArray(newArray)
        
        checkWinner()
    }

    const newSquare = (number) => {
        console.log(boardArray);
        return <Square
            value={boardArray[number]}
            number={number}
            onClick={() => handleClick(number)}
        />
    }
    
    return (
        <div className="board">
            {newSquare(0)}
            {newSquare(1)}
            {newSquare(2)}
            {newSquare(3)}  
            {newSquare(4)}
            {newSquare(5)}
            {newSquare(6)}
            {newSquare(7)}
            {newSquare(8)}
        </div>
    )
}

export default Board
