import React, { useState, useEffect } from 'react'
import ReactDOM from 'react-dom'
import Square from './Square'

const Board = () => {
    const [boardArray, setBoardArray] = useState(Array(9).fill(null))
    const [XNext, setXNext] = useState(true)

    // sjekker om noen har vunnet hver gang brettet rendres
    useEffect(() => {
        checkWinner()
    })

    
    // returnerer x eller y
    const checkWinner = ()  => {
        debugger;
        const vinnerLinjer = [
            [0,1,2],
            [3,4,5],
            [6,7,8],
            [0,3,6],
            [1,4,7],
            [2,5,8],
            [2,4,6],
            [0,4,8]
        ]

        // sjekk boararray om det har en av disse linjene med bare en verdi
        vinnerLinjer.forEach(linje => {
            debugger; 

            let firstValue = boardArray[linje[0]]
            let secondValue = boardArray[linje[1]]
            let thirdValue = boardArray[linje[2]]

            if (firstValue !== null && secondValue !== null && thirdValue !== null) {
                if (firstValue === secondValue && secondValue === thirdValue) {
                    if (firstValue === 'X') { 
                        alert("X VINNER")
                    } else {
                        alert("Y VINNER")
                    }
                }
            }
        })
        
    }

    const handleClick = (n) => {
        // checkWinner()

        const newArray = boardArray.slice()
        if (XNext) {
            newArray[n] = 'X'
        } else {
            newArray[n] = '0'
        }
        setXNext(!XNext)

        setBoardArray(newArray)
        
    }

    const newSquare = (number) => {
        console.log(boardArray);
        return <Square
            value={boardArray[number]}
            number={number}
            onClick={() => handleClick(number)}
        />
    }
    
    return (
        <div className="board">
            {newSquare(0)}
            {newSquare(1)}
            {newSquare(2)}
            {newSquare(3)}  
            {newSquare(4)}
            {newSquare(5)}
            {newSquare(6)}
            {newSquare(7)}
            {newSquare(8)}
        </div>
    )
}

export default Board
